  mounted: function mounted() {
    window.scrollTo(0, 0);
    var t = this;
    var gameCode = this.$route.params.id;
    this.setCookie('currentGameName', encodeURI(this.$route.meta.title), 0.5); //safari 不支持存中文 要编码 

    gamestatus(this, gameCode, function (res) {
      if (_Object$keys(res).length === 0) {
        alert("维护中");
        t.$router.push({
          path: "/index"
        });
        return;
      } //更新vuex 状态


      t.$store.dispatch("updatebetGameCode", gameCode);

      _getbettime(t, gameCode, t.time); //盘口资料


      _payRate(t, gameCode, "2", t.payrate); //赔率

    });
  },
  methods: {
    //   监听事件 : 监听玩法选择有无变动，若有则执行FN
    clickOff: function clickOff(clickOffValue, clickSubValue) {
      this.offTitle = clickOffValue;
      this.OffSubTitle = clickSubValue;
      this.currentName = this.gameMenu[clickOffValue].sub[clickSubValue].subName;
    },
    //监听如果开启选择玩法页面 就取消所有游戏选取
    childByValue: function childByValue() {
      this.gameMenu[this.offTitle].sub[this.OffSubTitle].classification.forEach(function (e) {
        e.option_slide_idx = null;
        e.classificationArr.forEach(function (i) {
          i.checked = false;
        });
      });
      this.option_slide_idx = null;
      this.quantity = 0;
      this.forbet = []; //暂存清空

      this.rebatRate = 0;
    },
    bundledatainit: function bundledatainit() {
      //资料初始
      var _t = this;

      if (_Object$keys(this.payrate.value).length > 0) {
        this.gameMenu.forEach(function (v) {
          v.sub[0].classification.forEach(function (q) {
            q.classificationArr.forEach(function (e) {
              _t.renderdata(e);
            });
          });
        });
      }

      this.mask = false;
    },
    renderdata: function renderdata(obj) {
      //玩法阵列资料绑定
      var _t = this;

      var _p = _t.payrate.value[obj.id];
      obj.odds = calPayRate(_p, 0);
      obj.odds2 = calPayRate2(_p, 0);
      obj.playTypeS = _p.playTypeS;
      obj.playTypeM = _p.playTypeM;
      _t.barMax = _p.rebatRate;
    },
    barrenderdata: function barrenderdata(obj) {
      //拉动bar 玩法赔率变动           
      var _p = this.payrate.value[obj.id];
      obj.odds = calPayRate(_p, this.rebatRate);
      obj.odds2 = calPayRate2(_p, this.rebatRate);
    },
    //选取的号码
    checkNum: function checkNum(Arr) {
      var _t = this;

      this.forbet = [];
      var checkedArr = Arr.filter(function (v, i) {
        return v.checked == true;
      });
      checkedArr.forEach(function (e) {
        var positions = ['', '', ''];

        if (e.id === 'ThreeAtTriple-All') {
          positions[0] = '1,2,3,4,5,6';
        }

        if (e.id === 'ThreeAtTriple-S' || e.id === 'TwoAtDouble-M') {
          positions[0] = e.play.substr(0, 1);
        }

        if (e.id === 'TwoAtDiff-G') {
          positions[0] = e.play.substr(0, 1) + ',' + e.play.substr(1, 1);
        }

        _t.forbet.push({
          uid: randomRangeId(10),
          id: e.id,
          play: e.play,
          name: _t.currentName,
          betMainInfo: {
            playTypeM: e.playTypeM,
            playTypeS: e.playTypeS,
            positions: positions,
            quantity: 1,
            payRate: e.odds,
            payRate2: e.odds2,
            rebatRate: _t.rebatRate,
            unitPrice: 0
          }
        });
      });
      var Plus = 0;
      this.forbet.forEach(function (v, i) {
        Plus += v.betMainInfo.quantity;
      });
      this.quantity = Plus;
    }
  },
  watch: {
    payrate: {
      deep: true,
      handler: function handler(nv, ov) {
        this.bundledatainit();
      }
    },
    rebatRate: function rebatRate(nv, ov) {
      var _t = this;

      this.rebatRate = nv; //画面、阵列更新

      this.gameMenu[this.offTitle].sub[0].classification.forEach(function (v) {
        v.classificationArr.forEach(function (e) {
          _t.barrenderdata(e);
        });
      }); //已选的下注 更新资料

      if (this.forbet.length > 0) {
        this.forbet.forEach(function (v, i) {
          var _p = _t.payrate.value[v.id];
          v.betMainInfo.payRate = calPayRate(_p, nv);
          v.betMainInfo.payRate2 = calPayRate2(_p, nv);
          v.betMainInfo.rebatRate = nv;
        });
      }
    }
  },
  destroyed: function destroyed() {
    var unWatch = this.$watch("payrate");
    unWatch(); // 手动注销watch

    this.childByValue();
    cleartimer();
  },
  computed: {
    donotTouch: function donotTouch() {
      return this.$store.state.dontTouch;
    }
  }
};
