import "core-js/modules/es6.function.name";
//
//
export default {
  name: "linkMemberRegistered",
  data: function data() {
    return {
      siteID: this.$global.siteID,
      loginID: this.$store.state.bet.loginID,
      urlMemberList: [],
      linkNum: null,
      linkStatus: false,
      linkStatusNum: null,
      linkStatusName: null
    };
  },
  mounted: function mounted() {
    this.urlList();
  },
  methods: {
    urlList: function urlList() {
      var t = this;

      var _webs = JSON.parse(this.getCookie("webs"));

      var _parm = {
        jsonString: {
          siteID: t.siteID,
          loginID: t.loginID
        },
        token: _webs.Authorization
      };
      t.$axios("T7UrlList", _parm, function (resp) {
        var json = JSON.parse(resp.data);
        console.log(json);
        var thisURL = window.location.href;
        var questionMark = thisURL.indexOf("?");
        var endSite = thisURL.length;
        t.linkNum = thisURL.substring(questionMark + 1, endSite);
        t.urlMemberList = json.data[t.linkNum];
        t.linkStatusNum = json.data[t.linkNum].status;

        if (t.linkStatusNum == 0) {
          t.linkStatusName = "停用";
        } else {
          t.linkStatusName = "正常";
        }
      });
    },
    clickStatus: function clickStatus(num) {
      var t = this;
      t.linkStatus = !t.linkStatus;

      if (num == 0) {
        t.linkStatusName = "停用";
        t.linkStatusNum = 0;
      } else {
        t.linkStatusName = "正常";
        t.linkStatusNum = 1;
      }
    },
    updateUrl: function updateUrl() {
      var _webs = JSON.parse(this.getCookie("webs"));

      var parm = {
        jsonString: {
          siteID: this.siteID,
          loginID: this.loginID,
          uid: this.urlMemberList.uid,
          name: this.urlMemberList.name,
          status: this.linkStatusNum
        },
        token: _webs.Authorization
      };
      this.$axios("T7UpdateUrl", parm, function (resp) {
        var json = JSON.parse(resp.data);

        if (json.error._error) {
          alert(json.error._error);
        } else {
          alert("成功");
          var t = this;
          var thisURL = window.location.href;
          var hashtag = thisURL.indexOf("#");
          var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkManagement";
          window.location.href = nextUrl;
        }
      });
    }
  }
};
