export default {
  name: "linkManagement",
  data: function data() {
    return {
      siteID: this.$global.siteID,
      loginID: this.$store.state.bet.loginID,
      urlMemberList: []
    };
  },
  mounted: function mounted() {
    this.urlList();
  },
  methods: {
    urlList: function urlList() {
      var t = this;

      var _webs = JSON.parse(this.getCookie("webs"));

      var _parm = {
        jsonString: {
          siteID: t.siteID,
          loginID: t.loginID
        },
        token: _webs.Authorization
      };
      t.$axios("T7UrlList", _parm, function (resp) {
        var json = JSON.parse(resp.data);
        console.log(json);
        t.urlMemberList = json.data;
      });
    },
    urlCreatePage: function urlCreatePage(code) {
      var thisURL = window.location.href;
      var hashtag = thisURL.indexOf("#");
      var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkMemberRegistered?" + code;
      window.location.href = nextUrl;
    },
    copylink: function copylink(code) {
      var input = document.querySelector("#js-copyURL");
      var thisURL = window.location.href;
      var hashtag = thisURL.indexOf("#");
      var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkMemberRegistered?" + code;
      input.setAttribute("value", nextUrl);
      input.select();

      if (document.execCommand("copy")) {
        document.execCommand("copy");
        alert("复制成功");
      }
    },
    openqrCode: function openqrCode(code) {
      var thisURL = window.location.href;
      var hashtag = thisURL.indexOf("#");
      var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkqrCode?" + code;
      window.location.href = nextUrl;
    },
    linkModify: function linkModify(Index) {
      var thisURL = window.location.href;
      var hashtag = thisURL.indexOf("#");
      var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkModify?" + Index;
      window.location.href = nextUrl;
    }
  }
};
