export default {
  data: function data() {
    return {
      siteID: this.$global.siteID,
      messageList: [],
      clickMsg: null,
      openStyle: {
        color: "#fff"
      }
    };
  },
  //點擊訊息
  methods: {
    msg: function msg(i) {
      this.clickMsg = i;
    }
  },
  mounted: function mounted() {
    var _this = this;

    var _web = JSON.parse(this.getCookie("webs"));

    var loginID = _web.loginID;
    var Authorization = _web.Authorization; //會員訊息API

    this.$axios("GetUserMessage", {
      jsonString: {
        siteID: this.siteID,
        loginID: loginID
      },
      token: Authorization
    }, function (resp) {
      var res = JSON.parse(resp.data);
      var list = res.data;

      for (var i = 0; i < list.length; i++) {
        _this.messageList.push(list[i]);
      }
    });
  }
};
