import Marquee from "@/components/Marquee.vue";
export default {
  name: "index",
  components: {
    Marquee: Marquee
  },
  data: function data() {
    return {
      mask: 0,
      siteID: this.$global.siteID,
      marqueelistTime: [],
      marqueelistNotice: [],
      notice: 0,
      messageSelected: undefined,
      gameType: 0,
      itemClicked: undefined,
      noContent: "yes",
      gameitem: [],
      game: this.$parent.menu
    };
  },
  mounted: function mounted() {
    var t = this;
    var _parm = {
      siteID: this.siteID
    };
    this.$axios("marquee", _parm, function (resp) {
      var json = JSON.parse(resp.data);
      json.data.forEach(function (v) {
        t.marqueelistNotice.push(v.title + ":" + v.content);
        var msgTime = v.msgTime.slice(0, 16).replace("T", " ");
        t.marqueelistTime.push(msgTime);
      });
    });
  },
  methods: {
    changeGameType: function changeGameType(changeType, gameType) {
      if (gameType == "gameType") {
        this.gameType = changeType;
      } else if (gameType == "sideGameType") {
        this.sideGameType = changeType;
      }

      this.itemClicked = undefined;
      this.sideItemClicked = undefined;
    },
    itemClick: function itemClick(item, type) {
      if (type == "index") {
        if (item == this.itemClicked) {
          this.itemClicked = undefined;
          return;
        }

        this.itemClicked = item;
      }

      if (type == "side") {
        if (item == this.sideItemClicked) {
          this.sideItemClicked = undefined;
          return;
        }

        this.sideItemClicked = item;
      }
    },
    footerOnIMG: function footerOnIMG(index) {
      if (this.footer[index].alias == this.webName) {
        var footerActiveIMG = this.footer[index].src.split(".");
        var footerActiveIMG_new = footerActiveIMG[0] + "_on." + footerActiveIMG[1];
        return footerActiveIMG_new;
      }

      return this.footer[index].src;
    },
    test: function test(item) {
      if (this.itemClicked == item) {
        var num = this.game[this.gameType].gameItem[item].sub.length;
        var range = Math.ceil(num / 2);

        if (range == 0) {
          this.noContent = "no";
        } else {
          this.noContent = "yes";
          var margin = range * 6.4 + "vmax";
          return "marginBottom:" + margin;
        }
      }
    }
  }
};
