export default {
  name: "cq9FISH",
  data: function data() {
    return {
      siteID: this.$global.siteID,
      gamedata: [],
      hotGameData: [],
      topGameData: [],
      gameSelect: 0,
      platform: 43,
      searchStatus: false,
      searchText: ""
    };
  },
  mounted: function mounted() {
    this.$store.state.searchStatus = false;
    this.$store.dispatch("setsearchStatus", this.$store.state.searchStatus);
    var t = this;

    var _webs = JSON.parse(this.getCookie("webs"));

    var gamedata = [];
    var hotGame = [];
    var topGame = [];
    var _parm = {
      siteID: this.siteID,
      platform: this.platform
    };
    var _parmRecommendGame = {
      siteID: this.siteID
    };
    var _parmTopGame = {
      siteID: this.siteID,
      loginID: this.$store.state.bet.loginID,
      token: _webs.Authorization
    };
    this.$axios("OverviewGameList", _parm, function (resp) {
      var json = JSON.parse(resp.data);
      var i;

      for (i = 0; i < json.data.length; i++) {
        if (json.data[i].spID == 48) {
          gamedata.push({
            spid: json.data[i].spID,
            gameName: json.data[i].gameName,
            gameID: json.data[i].gameID,
            platformType: json.data[i].platformType,
            GameImgUrl: "https://image.aliapple.net/images/games/" + json.data[i].platformType + "/" + json.data[i].gameID + ".png"
          });
        }
      }

      t.gamedata = gamedata;
    });
    this.$axios("RecommendGameList", _parmRecommendGame, function (resp) {
      var json = JSON.parse(resp.data);
      var i;

      for (i = 0; i < json.data.length; i++) {
        if (json.data[i].spID == 48) {
          hotGame.push({
            spid: json.data[i].spID,
            gameName: json.data[i].gameNameCN,
            gameID: json.data[i].gameID,
            mspID: json.data[i].mspID,
            GameImgUrl: "https://image.aliapple.net/images/games/" + json.data[i].mspID + "/" + json.data[i].gameID + ".png"
          });
        }

        t.hotGameData = hotGame;
      }
    });
    this.$axios("TopGame", _parmTopGame, function (resp) {
      var json = JSON.parse(resp.data);
      var i;

      for (i = 0; i < json.data.length; i++) {
        if (json.data[i].spID == 48) {
          topGame.push({
            spid: json.data[i].spID,
            gameName: json.data[i].gameNameCN,
            gameID: json.data[i].gameID,
            mspID: json.data[i].mspID,
            GameImgUrl: "https://image.aliapple.net/images/games/" + json.data[i].mspID + "/" + json.data[i].gameID + ".png"
          });
        }
      }

      t.topGameData = topGame;
    });
  },
  methods: {
    selectGame: function selectGame(num) {
      this.gameSelect = num;
    },
    clickgame: function clickgame(gameID) {
      var _webs = JSON.parse(this.getCookie("webs"));

      var _parm = {
        siteID: this.siteID,
        loginID: this.$store.state.bet.loginID,
        platform: this.platform,
        gameID: gameID,
        token: _webs.Authorization
      };
      this.$axios("GameGateway", _parm, function (resp) {
        var json = JSON.parse(resp.data);

        if (json.error._error) {
          alert(json.error._error);
          return;
        }

        window.open(json.info.openGameUrl);
      });
    }
  },
  computed: {
    searchFun: function searchFun() {
      this.searchStatus = this.$store.state.searchStatus;
      return this.searchStatus;
    },
    searchDataFun: function searchDataFun() {
      var searchText = this.searchText;
      var searchData = this.gamedata;
      var searchGameData = [];

      if (searchText != "") {
        var i;

        for (i = 0; i < searchData.length; i++) {
          if (searchData[i].gameName.indexOf(searchText) != -1) {
            searchGameData.push({
              spid: searchData[i].spID,
              gameName: searchData[i].gameName,
              gameID: searchData[i].gameID,
              platformType: searchData[i].platformType,
              GameImgUrl: "https://image.aliapple.net/images/games/" + searchData[i].platformType + "/" + searchData[i].gameID + ".png"
            });
          }
        }
      } else {
        searchGameData = this.gamedata;
      }

      return searchGameData;
    },
    searchHotFun: function searchHotFun() {
      var searchText = this.searchText;
      var searchData = this.hotGameData;
      var searchGameData = [];

      if (searchText != "") {
        var i;

        for (i = 0; i < searchData.length; i++) {
          if (searchData[i].gameName.indexOf(searchText) != -1) {
            searchGameData.push({
              spid: searchData[i].spid,
              gameName: searchData[i].gameName,
              gameID: searchData[i].gameID,
              mspID: searchData[i].mspID,
              GameImgUrl: "https://image.aliapple.net/images/games/" + searchData[i].mspID + "/" + searchData[i].gameID + ".png"
            });
          }
        }
      } else {
        searchGameData = this.hotGameData;
      }

      return searchGameData;
    },
    searchTopFun: function searchTopFun() {
      var searchText = this.searchText;
      var searchData = this.topGameData;
      var searchGameData = [];

      if (searchText != "") {
        var i;

        for (i = 0; i < searchData.length; i++) {
          if (searchData[i].gameName.indexOf(searchText) != -1) {
            searchGameData.push({
              spid: searchData[i].spid,
              gameName: searchData[i].gameName,
              gameID: searchData[i].gameID,
              mspID: searchData[i].mspID,
              GameImgUrl: "https://image.aliapple.net/images/games/" + searchData[i].mspID + "/" + searchData[i].gameID + ".png"
            });
          }
        }
      } else {
        searchGameData = this.topGameData;
      }

      return searchGameData;
    }
  }
};
