import QRCode from "qrcodejs2";
import { Script } from "vm";
export default {
  name: "linkqrCode",
  data: function data() {
    return {
      urlText: 0
    };
  },
  mounted: function mounted() {
    this.Qrcode();
  },
  methods: {
    Qrcode: function Qrcode() {
      var thisURL = window.location.href;
      var questionMark = thisURL.indexOf("?");
      var hashtag = thisURL.indexOf('#');
      var nextUrl = thisURL.substring(0, hashtag) + "#/agentCenter/linkMemberRegistered?";
      var endSite = thisURL.length;
      var linkCode = thisURL.substring(questionMark + 1, endSite);
      nextUrl = nextUrl + linkCode;
      var qrcode = new QRCode("qrcode_box", {
        // width: 100, //二维码的宽度
        // height: 100, //二维码的高度
        text: nextUrl,
        // 二维码地址
        colorDark: "#000",
        //二维码颜色
        colorLight: "#fff" //二维码背景颜色

      });
    }
  }
};
